<script>
export default {
  name: "NavigationBar",
  data: () => ({
    items: [
      {title: 'Home', icon: 'mdi-help', route: {path: '/'}},
      {title: 'Blogs', icon: 'mdi-help', route: {path: '/blogs/home'}},
    ],
  }),
  methods: {
    goto: (routeData) => {
      this.$router.push(routeData)
    }
  },
}
</script>

<style lang="scss" scoped>
@import "styles/themeData";

.navbar-nav {
  .nav-item {
    display: flex;
    align-items: stretch;
    justify-content: center;
    margin: 12px 20px;

    .nav-link {
      color: $semiDark;
      position: relative;

      &:hover {
        color: $secondary;
      }

      &::after {
        content: '';
        position: absolute;
        background: $primary;
        bottom: 0;
        width: 0;
        height: 1px;
        left: 0;
        transition: width .25s ease-in;
      }

